import timeimport sysimport gpioimport RPi.GPIO as GPIOclass Car:	def __init__(self):		self.stop = False		GPIO.setmode(GPIO.BOARD)		GPIO.setwarnings(False)		GPIO.setup(gpio.CAR_GPIO_PINS, GPIO.OUT)		GPIO.output(gpio.CAR_GPIO_ENX, GPIO.LOW)	def move_on(self):		''' 前进 '''		GPIO.output([gpio.GPIO_PIN_IN1, gpio.GPIO_PIN_IN2], (GPIO.LOW, GPIO.HIGH))	# 左侧车轮正转		GPIO.output([gpio.GPIO_PIN_IN3, gpio.GPIO_PIN_IN4], (GPIO.LOW, GPIO.HIGH))	# 右侧车轮正转	def move_back(self):		''' 后退 '''		GPIO.output([gpio.GPIO_PIN_IN1, gpio.GPIO_PIN_IN2], (GPIO.HIGH, GPIO.LOW))	# 左侧车轮反转		GPIO.output([gpio.GPIO_PIN_IN3, gpio.GPIO_PIN_IN4], (GPIO.HIGH, GPIO.LOW))	# 右侧车轮反转	def turn_left(self):		''' 左转 '''		GPIO.output([gpio.GPIO_PIN_IN1, gpio.GPIO_PIN_IN2], (GPIO.HIGH, GPIO.LOW))	# 左侧车轮反转		GPIO.output([gpio.GPIO_PIN_IN3, gpio.GPIO_PIN_IN4], (GPIO.LOW, GPIO.HIGH))	# 右侧车轮正转	def turn_right(self):		''' 右转 '''		GPIO.output([gpio.GPIO_PIN_IN1, gpio.GPIO_PIN_IN2], (GPIO.LOW, GPIO.HIGH))	# 左侧车轮正转		GPIO.output([gpio.GPIO_PIN_IN3, gpio.GPIO_PIN_IN4], (GPIO.HIGH, GPIO.LOW))	# 右侧车轮反转	def move_stop(self):		GPIO.output(gpio.CAR_GPIO_ENX, GPIO.LOW)		GPIO.cleanup()	def pwm_speed(self, ms):		''' PWM信号脉冲调速 '''		rate = 0.0003		while ms:			time.sleep(rate)			GPIO.output(gpio.CAR_GPIO_ENX, GPIO.LOW)			time.sleep(rate)			GPIO.output(gpio.CAR_GPIO_ENX, GPIO.HIGH)			ms = ms - 1		self.move_stop()if __name__ == '__main__':	car = Car()	if sys.argv[1] == 'on':		car.move_on()	if sys.argv[1] == 'back':		car.move_back()	if sys.argv[1] == 'left':		car.turn_left()	if sys.argv[1] == 'right':		car.turn_right()	car.pwm_speed(1000)